    #undef DOUBLE_CLICK
#endif
#ifdef ABSOLUTE
    #undef ABSOLUTE
#endif
#ifdef RELATIVE
    #undef RELATIVE
#endif
#ifdef DIFFERENCE
    #undef DIFFERENCE
#endif
#ifdef interface
    #undef interface
#endif
